import React, { useEffect, useState } from 'react';
import { Task } from '../../types/task';
import { TaskService } from '../../services/taskService';
import TaskForm from './TaskForm';

const TaskList: React.FC = () => {
    const [tasks, setTasks] = useState<Task[]>([]);
    const [error, setError] = useState<string | null>(null);

    const fetchTasks = async () => {
        try {
            const data = await TaskService.listTasks();
            console.log('Fetched tasks:', data);
            setTasks(data);
        } catch (err) {
            setError('Failed to load tasks');
            console.error('Error fetching tasks:', err);
        }
    };

    const handleDelete = async (id: number) => {
        console.log(`Deleting task with ID: ${id}`);  // Debug: Log the ID
        try {
            await TaskService.deleteTask(id);
            setTasks((prevTasks) => prevTasks.filter((task) => task.id !== id));
        } catch (error) {
            console.error('Failed to delete task:', error);
        }
    };

    const handleEdit = (task: Task) => {
        setEditingTask(task);  // Set the task being edited
    };

    const handleUpdate = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!editingTask) return;

        try {
            const updatedTask: TaskUpdate = {
                title: editingTask.title,
                description: editingTask.description,
                priority: editingTask.priority,
                dueDate: editingTask.dueDate,
                status: editingTask.status,
            };

            await TaskService.updateTask(editingTask.id, updatedTask);
            setEditingTask(null);  // Close the edit form
            fetchTasks();  // Reload tasks
        } catch (error) {
            console.error('Failed to update task:', error);
        }
    };


    useEffect(() => {
        fetchTasks();
    }, []);

    if (error) return <div>{error}</div>;

    return (
        <div>
            <h1>Task List</h1>
            <TaskForm onSuccess={fetchTasks} />  {/* Add TaskForm */}
            {tasks.map((task) => (
                <div key={task.id}>
                    <h3>{task.title}</h3>
                    <p>Task ID: {task.id}</p>
                    <p>{task.description}</p>
                    <button onClick={() => handleDelete(task.id)}>Delete</button>
                </div>
            ))}
        </div>
    );
};

export default TaskList;
